.wrapper {
  max-width: 1300px;
  margin: 0 auto;
}

.header {
  display: grid;
  height: 100vh;
  grid-template-columns: repeat(10, 1fr);
  grid-template-rows: repeat(10, 1fr);
}

.header__hero {
  grid-area: 1/1/-1/-1;
  background-image: url(d2220bf984a0d7880062707f0f0b5fd4.jpg);
  background-position: center;
  background-size: cover;
}

.header__logo {
  grid-area: 1/1/2/2;
  margin: 2rem 0 0 5rem;
  font-size: 3rem;
  font-weight: 700;
  color: white;
}

.section {
  height: 70vh;
  background-color: #F3F4F6;
  border-left: 1px solid #cdcdcd;
  display: grid;
  grid-template-columns: 1fr 1300px 1fr;
}

section:nth-child(odd) {
  background-color: white;
}

.nav {
  display: flex;
  flex-direction: column;
  position: fixed;
  top: 3%;
  right: 4.5%;
}
.nav__item {
  display: flex;
  justify-content: flex-end;
  color: white;
  padding: 5px;
  font-weight: 700;
}
.nav__item:hover {
  text-transform: uppercase;
}

footer {
  height: 300px;
  display: grid;
  justify-content: center;
  align-content: center;
}
footer h1 {
  font-size: 3rem;
}

.section-music {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  align-content: center;
}
.section-music__player {
  background-color: red;
}
.section-music__content p {
  font-weight: 700;
  font-size: 70rem;
}

* {
  box-sizing: border-box;
  margin: 0;
  font-family: "Major Mono Display", monospace;
}

body {
  margin: 0;
}

a {
  text-decoration: none;
}
