.wrapper {
  max-width: 1300px;
  margin: 0 auto;
  border-left: 1px solid #cdcdcd;
  z-index: 9999;
}

.header {
  display: grid;
  height: 100vh;
  grid-template-columns: repeat(13, 1fr);
  grid-template-rows: repeat(8, 1fr);
  border-left: 1px solid #cdcdcd;
}

.header__hero {
  grid-area: 2/3/8/14;
  background-image: url(d2220bf984a0d7880062707f0f0b5fd4.jpg);
  background-position: center;
  background-size: cover;
}
@media only screen and (max-width: 640px) {
  .header__hero {
    grid-area: 1/1/9/14;
  }
}

.header__logo {
  grid-area: 1/3/2/5;
  align-self: end;
  font-size: 3rem;
  font-weight: 400;
}
@media only screen and (max-width: 640px) {
  .header__logo {
    color: white;
  }
}

.header__top-links {
  grid-area: 1/8/2/14;
  justify-self: end;
  align-self: end;
  margin-bottom: 10px;
}
@media only screen and (max-width: 640px) {
  .header__top-links {
    display: none;
  }
}

.header__bot-links {
  grid-area: 8/3/9/14;
  margin-top: 10px;
}

.header__link {
  font-size: 0.9rem;
  font-weight: 700;
}
@media only screen and (max-width: 640px) {
  .header__link {
    color: white;
  }
}

.header__link:hover {
  text-transform: uppercase;
}

section {
  height: 600px;
  background-color: #F3F4F6;
  border-left: 1px solid #cdcdcd;
}

section:nth-child(odd) {
  background-color: white;
  border-left: none;
  border-left: 1px solid #cdcdcd;
}

.section--carousel {
  height: max-content;
  background-color: #192124 !important;
  border-right: 1px solid #cdcdcd;
}

.nav {
  background-color: white;
  display: flex;
  width: 100%;
  position: sticky;
  justify-content: space-evenly;
  align-items: center;
}
.nav__logo {
  margin-left: 0.5rem;
  grid-column: 1/2;
  font-size: 3rem;
  font-weight: 700;
  color: #1A2225;
}
.nav__link {
  font-size: 0.9rem;
  opacity: 1;
}
.nav__link:hover {
  text-transform: uppercase;
  opacity: 1;
}

.carousel-cell {
  margin-right: 20px;
  overflow: hidden;
}

.carousel-cell img {
  display: block;
  height: 300px;
}

.carousel-cell.is-selected img {
  filter: grayscale(100%);
  opacity: 1;
}

.flickity-button {
  background: none;
}
.flickity-button:hover {
  background: none;
}

.flickity-page-dots .dot {
  display: none;
}

@media screen and (min-width: 768px) {
  .carousel-cell img {
    height: 500px;
    border-radius: 0.1%;
    opacity: 0.7;
    filter: blur(5px) grayscale(50%);
    transition: opacity 0.3s, transform 0.3s, filter 0.3s;
  }

  .flickity-prev-next-button.previous {
    opacity: 0.4;
    left: -40px;
  }
  .flickity-prev-next-button.previous:hover {
    opacity: 1;
  }

  .flickity-prev-next-button.next {
    opacity: 0.4;
    right: -40px;
  }
  .flickity-prev-next-button.next:hover {
    opacity: 1;
  }
}
.flickity-prev-next-button.next, .flickity-prev-next-button.previous {
  display: none;
}

* {
  box-sizing: border-box;
  margin: 0;
  font-family: "Major Mono Display", monospace;
}

body {
  margin: 0;
}

a {
  text-decoration: none;
  color: #1A2225;
}
