.header {
  display: grid;
  height: 100vh;
  grid-template-columns: repeat(10, 1fr);
  grid-template-rows: repeat(10, 1fr);
}

.header__hero {
  grid-area: 1/1/-1/-1;
  background-image: url(d2220bf984a0d7880062707f0f0b5fd4.jpg);
  background-position: center;
  background-size: cover;
}

.header__logo {
  grid-area: 1/1/2/2;
  margin: 2rem 0 0 5rem;
  font-size: 3rem;
  font-weight: 700;
  color: white;
}

.section {
  margin: 0 auto;
  background-color: #F3F4F6;
}
.section h1 {
  font-size: 1.7rem;
}
.section p {
  font-family: Roboto;
  line-height: 1.4;
  font-weight: 400;
  text-align: justify;
}

section:nth-child(odd) {
  background-color: white;
}

.nav {
  display: flex;
  flex-direction: column;
  position: fixed;
  top: 3%;
  right: 4.5%;
}
.nav__item {
  display: flex;
  justify-content: flex-end;
  color: white;
  padding: 5px;
  font-weight: 700;
  transition: color 1s;
  text-shadow: -1px -1px 1px rgba(255, 255, 255, 0.1), 1px 1px 1px rgba(0, 0, 0, 0.5);
}
.nav__item:hover {
  text-transform: uppercase;
}

.black {
  transition: color 1s;
  color: black;
}

footer {
  height: 300px;
  display: grid;
  justify-content: center;
  align-content: center;
}
footer h1 {
  font-size: 3rem;
}

.draw-border {
  box-shadow: inset 0 0 0 4px #E5DCD3;
  color: #E5DCD3;
  transition: color 0.25s 0.0833333333s;
  position: relative;
}
.draw-border::before, .draw-border::after {
  border: 0 solid transparent;
  box-sizing: border-box;
  content: "";
  pointer-events: none;
  position: absolute;
  width: 0;
  height: 0;
  bottom: 0;
  right: 0;
}
.draw-border::before {
  border-bottom-width: 4px;
  border-left-width: 4px;
}
.draw-border::after {
  border-top-width: 4px;
  border-right-width: 4px;
}
.draw-border:hover {
  color: #1C2226;
}
.draw-border:hover::before, .draw-border:hover::after {
  border-color: #1C2226;
  transition: border-color 0s, width 0.25s, height 0.25s;
  width: 100%;
  height: 100%;
}
.draw-border:hover::before {
  transition-delay: 0s, 0s, 0.25s;
}
.draw-border:hover::after {
  transition-delay: 0s, 0.25s, 0s;
}

.btn {
  background: none;
  border: none;
  cursor: pointer;
  line-height: 1.5;
  font: 700 1.2rem "Roboto Slab", sans-serif;
  padding: 1em 2em;
  letter-spacing: 0.05rem;
}
.btn:focus {
  outline: none;
}

.section-music {
  margin: auto;
  max-width: 1300px;
  height: 550px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  align-content: center;
  grid-gap: 1rem;
}
.section-music__player {
  justify-self: end;
}
.section-music__content {
  display: grid;
  justify-self: start;
  width: 350px;
}

#videos {
  background: url(cca63969b43032e3204e9b9b9f90c649.png);
  background-attachment: fixed;
}

.section-videos {
  max-width: 1300px;
  height: 550px;
  background-attachment: fixed;
  display: grid;
  margin: auto;
}

.section-shows {
  height: 550px;
}

* {
  box-sizing: border-box;
  margin: 0;
  font-family: "Major Mono Display", monospace;
  scroll-behavior: smooth;
}

body {
  margin: 0;
}

a {
  text-decoration: none;
}
